import { createStore } from 'vuex'

export default createStore({
  state: {
    counter: 0,
    pasteles: [
      { id: 1, nombre: 'Pastel de Fresa', precio: '$10.00', imagen: 'https://www.recetasdecocina.com/wp-content/uploads/2018/10/pastel-de-fresa-1.jpg' },
      { id: 2, nombre: 'Pastel de Chocolate', precio: '$10.00', imagen: 'https://www.recetasdecocina.com/wp-content/uploads/2018/10/pastel-de-chocolate-1.jpg' },
      { id: 3, nombre: 'Pastel de Dulce de Leche', precio: '$10.00', imagen: 'https://www.recetasdecocina.com/wp-content/uploads/2018/10/pastel-de-dulce-de-leche-1.jpg' },
      { id: 4, nombre: 'Pastel de Dulce de Leche', precio: '$10.00', imagen: 'https://www.recetasdecocina.com/wp-content/uploads/2018/10/pastel-de-dulce-de-leche-1.jpg' },
    ],
  },
  getters: {
    //recuperar datos que tenemos en el state
    square(state) {
      return state.counter * state.counter;
    },
    //recuperar datos que tenemos en el state
    getPasteles(state) {
      return state.pasteles;
    },
  },
  mutations: {
    //mutations con commit y actions con dispatch
    increment (state, random) {
      state.counter += random
    },
    decrement (state, random) {
      state.counter -= random
    },
  },
  actions: {
    //a diferencia de las mutaciones aqui se puede consultar codigo asincrono
    //https://www.random.org/integers/?num=10&min=1&max=6&col=1&base=10&format=plain&rnd=new
    async increment ( { commit } ) {
      const res = await fetch('https://www.random.org/integers/?num=1&min=1&max=5&col=1&base=10&format=plain&rnd=new');
      const results = await res.json();
      console.log(results);
      commit('increment', results);
    },
    async decrement ( { commit } ) {
      const res = await fetch('https://www.random.org/integers/?num=1&min=1&max=5&col=1&base=10&format=plain&rnd=new');
      const results = await res.json();
      console.log(results);
      commit('decrement', results);
    },
  },
  modules: {
  }
})
