import { createStore } from 'vuex'

export default createStore({
  state: {
    counter: 0
  },
  getters: {
  },
  mutations: {
    //mutations con commit y actions con dispatch
    increment (state) {
      state.counter++
    },
    decrement (state) {
      state.counter--
    }
  },
  actions: {
    //a diferencia de las mutaciones aqui se puede consultar codigo asincrono
    //https://www.random.org/integers/?num=10&min=1&max=6&col=1&base=10&format=plain&rnd=new
    async increment () {
      const res = await fetch('https://www.random.org/integers/?num=1&min=1&max=1000&col=1&base=10&format=plain&rnd=new');
      const results = await res.json();
      console.log(results);
      return results;
    },
    async decrement () {
      const res = await fetch('https://www.random.org/integers/?num=1&min=1&max=1000&col=1&base=10&format=plain&rnd=new');
      const results = await res.json();
      console.log(results);
      return results;
    }
  },
  modules: {
  }
})
